url_list = []

for hyperlink in hyperlink_list:
    m = re.match(r'([a-zA-Z0-9:/\.-]*)([^a-zA-Z0-9:/\.-]*)([a-zA-Z0-9:/\.-]*)', hyperlink)
    if 3 != len(m.groups()): continue
    url_list.append(m.group(1)+parse.quote(m.group(2))+m.group(3))

for i, url in enumerate(url_list):
    num = str(i+530)
    filename = './mp3/Taiwanese/'+num+'.mp3'
    print(url, filename)
    try:
        request.urlretrieve(url, filename)
    except:
        print('    Failed downloading: %s' % url)
